import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import '../styles/weekly_best.css';

const WeeklyBest = () => {
  const [currentSlide, setCurrentSlide] = useState(0);
  const navigate = useNavigate();

  const products = [
    {
      id: 1,
      image: "/images/weekly_best/1.jpg",
      title: "석류 꽃이 피었는지",
      price: 20000,
      discount: 10,
      isNew: true
    },
    {
      id: 2,
      image: "/images/weekly_best/2.jpg",
      title: "ESV한영해설성경 원단 다크블루",
      price: 19000,
      discount: 10,
      isNew: true
    },
    {
      id: 3,
      image: "/images/weekly_best/3.jpg",
      title: "울뉴굿데이성경 초미니 개역개정 새찬송가",
      price: 12000,
      discount: 10,
      isNew: true
    },
    {
      id: 4,
      image: "/images/weekly_best/4.jpg",
      title: "성서원 NIV한영해설성경 중합본 개역개정",
      price: 25000,
      discount: 10,
      isNew: true
    },
    {
      id: 5,
      image: "/images/weekly_best/5.jpg",
      title: "아가페큰글성경 소합본 개역개정",
      price: 21000,
      discount: 10,
      isNew: true
    }
  ].map(product => ({
    ...product,
    originalPrice: `${product.price.toLocaleString()}원`,
    salePrice: product.price * (1 - product.discount/100)
  }));

  const handleProductClick = (productId) => {
    navigate(`/product/${productId}`);
  };

  const itemsPerSlide = 4;
  const totalSlides = Math.ceil(products.length / itemsPerSlide);

  const nextSlide = () => {
    setCurrentSlide((prev) => (prev + 1) % totalSlides);
  };

  const prevSlide = () => {
    setCurrentSlide((prev) => (prev - 1 + totalSlides) % totalSlides);
  };

  return (
    <div className="container mx-auto px-4">
      <h2 className="text-2xl font-bold mb-6">Weekly Best</h2>
      <div className="relative">
        <div className="overflow-hidden">
          <div
            className="flex transition-transform duration-300 ease-in-out"
            style={{
              transform: `translateX(-${currentSlide * 100}%)`,
            }}
          >
            {products.map((product, index) => (
              <div
                key={index}
                className="w-1/4 flex-shrink-0 px-2"
                onClick={() => handleProductClick(index + 1)}
              >
                <div className="bg-white rounded-lg shadow-md overflow-hidden">
                  {/* 이미지 컨테이너에 aspect ratio 적용 */}
                  <div className="relative aspect-[3/4] w-full">
                    <img
                      src={product.image}
                      alt={product.title}
                      className="absolute w-full h-full object-contain"
                    />
                  </div>
                  <div className="p-4">
                    <h3 className="text-lg font-semibold mb-2 truncate">
                      {product.title}
                    </h3>
                    <div className="flex justify-between items-center">
                      <div>
                        <p className="text-gray-500 line-through">
                          {product.originalPrice}
                        </p>
                        <p className="text-red-600 font-bold">
                          {Math.round(product.salePrice).toLocaleString()}원
                        </p>
                      </div>
                      {product.isNew && (
                        <span className="bg-blue-500 text-white px-2 py-1 rounded-full text-sm">
                          NEW
                        </span>
                      )}
                    </div>
                  </div>
                </div>
              </div>
            ))}
          </div>
        </div>
        {/* 네비게이션 버튼 */}
        <button
          onClick={() => setCurrentSlide(prev => (prev - 1 + totalSlides) % totalSlides)}
          className="absolute left-0 top-1/2 transform -translate-y-1/2 bg-white p-2 rounded-full shadow-md"
        >
          ←
        </button>
        <button
          onClick={nextSlide}
          className="absolute right-0 top-1/2 transform -translate-y-1/2 bg-white p-2 rounded-full shadow-md"
        >
          →
        </button>
      </div>
    </div>
  );
};

export default WeeklyBest;
