import React, { useState } from 'react';
import '../styles/product_detail.css';

function ProductDetail() {
  // 상품 수량과 선택된 탭을 관리하는 상태
  // quantity: 구매할 상품의 수량을 관리 (기본값: 1)
  // selectedTab: 현재 선택된 탭을 관리 (detail, info, reviews)
  const [quantity, setQuantity] = useState(1);
  const [selectedTab, setSelectedTab] = useState('detail');

  // 상품 상세 정보 데이터 객체
  // 실제 구현 시에는 API 호출이나 props를 통해 데이터를 받아옴
  const product = {
    id: 1, // 상품 고유 식별자
    title: "울뉴굿데이성경 초미니 개역개정 새찬송가", // 상품명
    subTitle: "휴대하기 편한 초미니 사이즈의 성경", // 상품 부제목
    price: 30000, // 정가
    salePrice: 27000, // 할인가
    // 상품 이미지 배열 (메인 이미지와 썸네일용)
    // 첫 번째 이미지가 메인 이미지로 사용됨
    images: [
      "/images/products/detail1.jpg",
      "/images/products/detail2.jpg",
      "/images/products/detail3.jpg"
    ],
    description: "가볍고 휴대하기 편한 초미니 사이즈의 성경입니다.", // 상품 설명
    // 상품 상세 정보 객체 (상품정보 탭에서 표시)
    details: {
      publisher: "한국복음서원", // 출판사
      publishDate: "2024-01-15", // 출간일
      size: "90x130mm", // 제품 크기
      pages: "1,024페이지", // 페이지 수
      cover: "양장제본", // 제본 형태
      isbn: "978-89-7531-123-4" // ISBN
    }
  };

  // 수량 변경 핸들러 함수
  // value: 증가(1) 또는 감소(-1)
  // 최소 수량 1개 보장
  const handleQuantityChange = (value) => {
    const newQuantity = quantity + value;
    if (newQuantity >= 1) {
      setQuantity(newQuantity);
    }
  };

  // 가격 포맷팅 함수
  // 입력된 숫자를 천 단위 콤마가 있는 문자열로 변환
  // 예: 27000 -> "27,000"
  const formatPrice = (price) => {
    return price.toString().replace(/\B(?=(\d{3})+(?!\d))/g, ",");
  };

  // 총 상품 금액 계산 함수
  // 할인가 * 수량을 계산하고 포맷팅하여 반환
  const calculateTotalPrice = () => {
    return formatPrice(product.salePrice * quantity);
  };

  return (
    <div className="product-detail-container">
      {/* 상품 기본 정보 섹션 - 이미지 갤러리와 상품 정보를 포함 */}
      <div className="product-basic-info">
        {/* 이미지 갤러리 섹션 - 메인 이미지와 썸네일 목록 */}
        <div className="product-gallery">
          {/* 메인 이미지 표시 영역 */}
          <div className="main-image">
            <img src={product.images[0]} alt={product.title} />
          </div>
          {/* 썸네일 이미지 목록 - 클릭 시 메인 이미지로 전환 가능 */}
          <div className="thumbnail-list">
            {product.images.map((image, index) => (
              <button key={index} className="thumbnail-item">
                <img src={image} alt={`${product.title} ${index + 1}`} />
              </button>
            ))}
          </div>
        </div>

        {/* 상품 정보 섹션 - 가격, 수량, 구매 버튼 등 */}
        <div className="product-info">
          {/* 상품명과 부제목 표시 */}
          <h1 className="product-title">{product.title}</h1>
          <p className="product-subtitle">{product.subTitle}</p>

          {/* 가격 정보 표시 영역 */}
          <div className="price-info">
            {/* 할인가가 있을 경우에만 원래 가격 표시 (취소선) */}
            {product.price !== product.salePrice && (
              <span className="original-price">{formatPrice(product.price)}원</span>
            )}
            <span className="sale-price">{formatPrice(product.salePrice)}원</span>
          </div>

          {/* 수량 선택 컨트롤 - 증가/감소 버튼과 수량 표시 */}
          <div className="quantity-selector">
            <span className="label">수량</span>
            <div className="quantity-controls">
              <button onClick={() => handleQuantityChange(-1)}>-</button>
              <span className="quantity">{quantity}</span>
              <button onClick={() => handleQuantityChange(1)}>+</button>
            </div>
          </div>

          {/* 총 상품 금액 표시 영역 */}
          <div className="total-price">
            <span className="label">총 상품 금액</span>
            <span className="price">{calculateTotalPrice()}원</span>
          </div>

          {/* 구매 관련 버튼 영역 */}
          <div className="purchase-buttons">
            <button className="cart-button">장바구니</button>
            <button className="buy-button">바로구매</button>
          </div>
        </div>
      </div>

      {/* 상세 정보 탭 섹션 - 상품상세/상품정보/리뷰 탭 */}
      <div className="product-detail-tabs">
        {/* 탭 버튼 그룹 - 각 탭을 선택할 수 있는 버튼들 */}
        <div className="tab-buttons">
          <button
            className={`tab-button ${selectedTab === 'detail' ? 'active' : ''}`}
            onClick={() => setSelectedTab('detail')}
          >
            상품상세
          </button>
          <button
            className={`tab-button ${selectedTab === 'info' ? 'active' : ''}`}
            onClick={() => setSelectedTab('info')}
          >
            상품정보
          </button>
          <button
            className={`tab-button ${selectedTab === 'reviews' ? 'active' : ''}`}
            onClick={() => setSelectedTab('reviews')}
          >
            리뷰
          </button>
        </div>

        {/* 탭 컨텐츠 영역 - 선택된 탭에 따라 다른 내용 표시 */}
        <div className="tab-content">
          {/* 상품 상세 설명 탭 - 상품 설명과 상세 이미지 */}
          {selectedTab === 'detail' && (
            <div className="detail-content">
              <p>{product.description}</p>
              {/* 추가 상세 이미지 및 설명이 들어갈 자리 */}
            </div>
          )}

          {/* 상품 정보 탭 - 상품 상세 정보를 테이블로 표시 */}
          {selectedTab === 'info' && (
            <div className="info-content">
              <table className="product-info-table">
                <tbody>
                  {/* 상품 상세 정보를 테-값 쌍으로 테이블에 표시 */}
                  {Object.entries(product.details).map(([key, value]) => (
                    <tr key={key}>
                      <th>{key}</th>
                      <td>{value}</td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          )}

          {/* 리뷰 탭 - 상품 리뷰 목록 표시 */}
          {selectedTab === 'reviews' && (
            <div className="reviews-content">
              {/* 리뷰 컴포넌트가 들어갈 자리 - 향후 구현 예정 */}
              <p>리뷰 내용이 들어갈 영역입니다.</p>
            </div>
          )}
        </div>
      </div>
    </div>
  );
}

export default ProductDetail;
