import React, { useState, useEffect } from 'react';
import '../styles/section.css';
import Header from './header';
import NavBar from './nav_bar';
import Footer from './footer';

function Section() {
  const [currentSlide, setCurrentSlide] = useState(0);
  const [isPaused, setIsPaused] = useState(false);

  const slides = [
    {
      id: 1,
      title: "신간도서",
      subtitle: "올 한 해 가장 사랑받은 책",
      year: "2025",
      image: "/flower.png", // 트로피 이미지 경로
      bgColor: "#f6f5ef",
      tag: "Special"
    },
    {
      id: 2,
      title: "신간도서",
      subtitle: "올 한 해 가장 사랑받은 책을 추천한다면?",
      year: "2025",
      image: "/logo512.png", // 트로피 이미지 경로
      bgColor: "#f6f5ef",
      tag: "Special"
    },
    {
      id: 3,
      title: "신간도서",
      subtitle: "올 한 해 가장 사랑받은 책은 뭘까?",
      year: "2025",
      image: "/logo192.png", // 트로피 이미지 경로
      bgColor: "#f6f5ef",
      tag: "Special"
    },
    // 추가 슬라이드 데이터...
  ];

  useEffect(() => {
    let interval;
    if (!isPaused) {
      interval = setInterval(() => {
        setCurrentSlide((prev) => (prev + 1) % slides.length);
      }, 4000);
    }
    return () => clearInterval(interval);
  }, [isPaused, slides.length]);

  const goToSlide = (index) => {
    setCurrentSlide(index);
  };

  const formatTime = (seconds) => {
    const minutes = Math.floor(seconds / 60);
    const remainingSeconds = seconds % 60;
    return `${String(minutes).padStart(2, '0')}:${String(remainingSeconds).padStart(2, '0')}`;
  };

  return (
    <>
      <section className="carousel-section">
        <div 
          className="carousel-container"
          onMouseEnter={() => setIsPaused(true)}
          onMouseLeave={() => setIsPaused(false)}
        >
          {slides.map((slide, index) => (
            <div 
              key={slide.id}
              className={`carousel-slide ${index === currentSlide ? 'active' : ''}`}
              style={{ 
                backgroundColor: slide.bgColor,
                color: '#000000'
              }}
            >
              <div className="slide-content">
                <div className="text-content">
                  <span className="tag">{slide.tag}</span>
                  <h2 className="title">{slide.title}</h2>
                  <p className="subtitle">{slide.subtitle}</p>
                  <div className="year">{slide.year}</div>
                </div>
                <div className="image-content">
                  <img src={slide.image} alt={slide.title} />
                </div>
              </div>
            </div>
          ))}

          {/* 컨트롤 버튼 */}
          <div className="carousel-controls">
            <button 
              className="pause-button"
              onClick={() => setIsPaused(!isPaused)}
            >
              {isPaused ? "▶" : "II"}
            </button>
            <div className="progress-container">
              {slides.map((_, index) => (
                <button
                  key={index}
                  className={`progress-dot ${index === currentSlide ? 'active' : ''}`}
                  onClick={() => goToSlide(index)}
                />
              ))}
            </div>
            <div className="timer">
              {formatTime(33)} 전체보기
            </div>
          </div>
        </div>
      </section>
    </>
  );
}

export default Section;
