import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './weekly_best.css';

const WeeklyBest = () => {
  const [currentSlide, setCurrentSlide] = useState(0);
  const navigate = useNavigate();

  const products = [
    {
      id: 1,
      image: "/images/weekly_best/1.jpg",
      title: "석류 꽃이 피었는지",
      price: 20000,
      discount: 10,
      isNew: true
    },
    {
      id: 2,
      image: "/images/weekly_best/2.jpg",
      title: "ESV한영해설성경 원단 다크블루",
      price: 19000,
      discount: 10,
      isNew: true
    },
    {
      id: 3,
      image: "/images/weekly_best/3.jpg",
      title: "울뉴굿데이성경 초미니 개역개정 새찬송가",
      price: 12000,
      discount: 10,
      isNew: true
    },
    {
      id: 4,
      image: "/images/weekly_best/4.jpg",
      title: "성서원 NIV한영해설성경 중합본 개역개정",
      price: 25000,
      discount: 10,
      isNew: true
    },
    {
      id: 5,
      image: "/images/weekly_best/5.jpg",
      title: "아가페큰글성경 소합본 개역개정",
      price: 21000,
      discount: 10,
      isNew: true
    }
  ].map(product => ({
    ...product,
    originalPrice: `${product.price.toLocaleString()}원`,
    salePrice: product.price * (1 - product.discount/100)
  }));

  const handleProductClick = (productId) => {
    navigate(`/product/${productId}`);
  };

  const itemsPerSlide = 4;
  const totalSlides = Math.ceil(products.length / itemsPerSlide);

  const nextSlide = () => {
    setCurrentSlide((prev) => (prev + 1) % totalSlides);
  };

  const prevSlide = () => {
    setCurrentSlide((prev) => (prev - 1 + totalSlides) % totalSlides);
  };

  return (
    // 전체 컨테이너: 최대 너비 1200px, 좌우 패딩 적용
    <div className="weekly-best-container">
      {/* 섹션 제목 */}
      <h2 className="weekly-best-title">주간 베스트</h2>

      {/* 슬라이더 메인 컨테이너 */}
      <div className="slider-container">
        {/* 슬라이더 오버플로우 처리를 위한 래퍼 */}
        <div className="slider-wrapper">
          {/* 실제 슬라이딩되는 트랙
              transform: translateX로 슬라이드 이동 처리
              currentSlide: 현재 슬라이드 인덱스 (0부터 시작) */}
          <div 
            className="slider-track"
            style={{
              transform: `translateX(-${currentSlide * 100}%)`,
            }}
          >
            {/* 상품 목록을 map으로 순회하여 카드 형태로 표시 */}
            {products.map((product, index) => (
              // 개별 상품 카드: 클릭 시 상세 페이지로 이동
              <div
                key={index}
                className="product-card"
                onClick={() => handleProductClick(index + 1)}
              >
                {/* 이미지 컨테이너: aspect-ratio 1:1 유지 */}
                <div className="image-container">
                  {/* 상품 이미지: object-fit contain으로 비율 유지 */}
                  <img
                    src={product.image}
                    alt={product.title}
                    className="product-image"
                  />
                </div>
                <div className="product-info">
                  <h3 className="product-title">{product.title}</h3>
                  <div className="price-container">
                    <p className="original-price">{product.originalPrice}</p>
                    <p className="sale-price">
                      {Math.round(product.salePrice).toLocaleString()}원
                    </p>
                  </div>
                  {product.isNew && (
                    <span className="new-badge">NEW</span>
                  )}
                </div>
              </div>
            ))}
          </div>
        </div>
        <button 
          onClick={() => setCurrentSlide(prev => (prev - 1 + totalSlides) % totalSlides)}
          className="nav-button prev"
        >
          ←
        </button>
        <button 
          onClick={nextSlide}
          className="nav-button next"
        >
          →
        </button>
      </div>
    </div>
  );
};

export default WeeklyBest;
